 */
function getColorScheme() {
	// set empty object
	var colorsJSON = {};
	// get all colors
	$('#designTab input.spectrumField').each(function() {
		colorsJSON[this.name] = this.value;
	});
	// print the colors
	console.log(JSON.stringify(colorsJSON));
}

